import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const client_id = "e46d40a2647a8388c2f5";
const client_secret = "79b90a1877131c334f913d5665f308cdab863304";
const user = "tweneboah";
export const fetchReposAction = createAsyncThunk(
  "repos/list",
  async (data, { rejectWithValue, getState, dispatch }) => {
    try {
      const { data } = await axios.get(
        `https://api.github.com/users/${user}?client_id=${client_id}&client_secret=${client_secret}`
      );

      return data;
    } catch (error) {}
  }
);

const reposSlices = createSlice({
  name: "repos",
  initialState: {},
  extraReducers: builder => {
    builder.addCase(fetchReposAction.pending, (state, action) => {
      state.loading = true;
    });
    builder.addCase(fetchReposAction.fulfilled, (state, action) => {
      console.log(action);
      state.reposList = action?.payload;
      state.loading = false;
    });
    builder.addCase(fetchReposAction.rejected, (state, action) => {
      state.loading = false;
      state.error = action.payload;
    });
  },
});

export default reposSlices.reducer;
