import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";

const client_id = "fc5d09568704b08440a4";
const client_secret = "d9c673dda3d8219b9187d9a1857774673c7f8cdd";

export const fetchReposAction = createAsyncThunk(
  "repos/list",
  async (user, { rejectWithValue, getState, dispatch }) => {
    try {
      const { data } = await axios.get(
        `https://api.github.com/users/${user}/repos?per_page=700&sort=desc&client_id=${client_id}&client_secret=${client_secret}`
      );

      return data;
    } catch (error) {
      if (!error?.response) {
        throw error;
      }
      return rejectWithValue(error.response.data);
    }
  }
);

export const fetchProfileAction = createAsyncThunk(
  "profile/list",
  async (user, { rejectWithValue, getState, dispatch }) => {
    try {
      const { data } = await axios.get(
        `https://api.github.com/users/${user}?client_id=${client_id}&client_secret=${client_secret}`
      );

      return data;
    } catch (error) {
      if (!error?.response) {
        throw error;
      }
      return rejectWithValue(error.response.data);
    }
  }
);

const reposSlices = createSlice({
  name: "repos",
  initialState: {},
  extraReducers: builder => {
    builder.addCase(fetchReposAction.pending, (state, action) => {
      state.loading = true;
    });
    builder.addCase(fetchReposAction.fulfilled, (state, action) => {
      state.reposList = action?.payload;
      state.loading = false;
    });
    builder.addCase(fetchReposAction.rejected, (state, action) => {
      state.loading = false;
      state.error = action.payload;
    });
    builder.addCase(fetchProfileAction.pending, (state, action) => {
      state.profileLoading = true;
    });
    builder.addCase(fetchProfileAction.fulfilled, (state, action) => {
      console.log(action);
      state.profile = action?.payload;
      state.profileLoading = false;
    });
    builder.addCase(fetchProfileAction.rejected, (state, action) => {
      state.profileLoading = false;
      state.error = action.payload;
    });
  },
});

export default reposSlices.reducer;
